sys_script_include.script:
var GetEmailAddress = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	// Define the getEmail function.  
	// Create a GlideRecord for the User table.
	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
	// Return the email address for the requested record
	getEmail: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},

getRequestedForDetails: function() {
		var userSysId = this.getParameter('sysparm_user');
		var userRecord = new GlideRecord("sys_user");
			userRecord.addQuery('sys_id',userSysId);
			userRecord.query();

		if(userRecord.get(userSysId)){
			var result = this.newItem("result");
			result.setAttribute('email', userRecord.getValue('email'));
			result.setAttribute('phone', userRecord.getValue('phone'));
			result.setAttribute('title', userRecord.getValue('title'));
			result.setAttribute('department', userRecord.getDisplayValue('department'));
			result.setAttribute('userID', userRecord.getValue('user_name'));
		//	return phone, email;

		}



		//userRecord.get(this.getParameter('sysparm_userID'));
		//return userRecord.email + '' + userRecord.phone;
	},
type: 'GetEmailAddress'
});
